SELECT MIN("movie_info_idx"."id") as agg_0 FROM "movie_info_idx" LEFT OUTER JOIN "info_type" ON "movie_info_idx"."info_type_id" = "info_type"."id" LEFT OUTER JOIN "title" ON "movie_info_idx"."movie_id" = "title"."id"  WHERE "title"."title" LIKE '%th%e%' AND ("info_type"."info" = '%quotes%' OR "info_type"."info" IN ('LD status of availablility', 'LD supplement', 'LD subtitles', 'LD video standard', 'runtimes', 'LD quality of source', 'LD number of sides', 'LD sharpness') OR "info_type"."info" NOT LIKE '%rank%') AND "movie_info_idx"."movie_id" <= 1741783;