SELECT MAX("company_name"."id") as agg_0, MIN("movie_companies"."company_id") as agg_1 FROM "movie_companies" LEFT OUTER JOIN "title" ON "movie_companies"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_info_idx" ON "title"."id" = "movie_info_idx"."movie_id" LEFT OUTER JOIN "company_name" ON "movie_companies"."company_id" = "company_name"."id"  WHERE "company_name"."country_code" NOT LIKE '%[it]%' AND ("movie_info_idx"."info" LIKE '%7.%1%' OR "movie_info_idx"."info" LIKE '%7.%5%' OR "movie_info_idx"."info" LIKE '%7%.1%') AND ("title"."series_years" IN ('2006-????', '2008-????', '2005-????', '2003-????', '2004-????', '2012-????') OR "title"."kind_id" BETWEEN 3 AND 7) AND "title"."title" NOT LIKE '%The%' AND ("movie_companies"."movie_id" <= 2436172 OR ("movie_companies"."movie_id" <= 1934894 AND "movie_companies"."movie_id" BETWEEN 2331475 AND 2375812));