SELECT COUNT(*) as agg_0 FROM "name" LEFT OUTER JOIN "person_info" ON "name"."id" = "person_info"."person_id" LEFT OUTER JOIN "aka_name" ON "name"."id" = "aka_name"."person_id" LEFT OUTER JOIN "cast_info" ON "aka_name"."id" = "cast_info"."person_id" LEFT OUTER JOIN "title" ON "cast_info"."movie_id" = "title"."id" LEFT OUTER JOIN "char_name" ON "cast_info"."person_role_id" = "char_name"."id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id"  WHERE "cast_info"."movie_id" >= 1013986 AND ("title"."kind_id" BETWEEN 7 AND 8 OR "title"."kind_id" BETWEEN 3 AND 7 OR "title"."kind_id" <= 7) AND "kind_type"."kind" LIKE '%tv%' AND "person_info"."person_id" IS NOT NULL AND "char_name"."id" IS NOT NULL;