SELECT SUM("title"."episode_of_id") as agg_0 FROM "person_info" LEFT OUTER JOIN "name" ON "person_info"."person_id" = "name"."id" LEFT OUTER JOIN "aka_name" ON "name"."id" = "aka_name"."person_id" LEFT OUTER JOIN "cast_info" ON "aka_name"."id" = "cast_info"."person_id" LEFT OUTER JOIN "char_name" ON "cast_info"."person_role_id" = "char_name"."id" LEFT OUTER JOIN "title" ON "cast_info"."movie_id" = "title"."id"  WHERE "aka_name"."name_pcode_cf" LIKE '%A5362%' AND "cast_info"."id" <= 12797072 AND "cast_info"."note" NOT LIKE '%(arc%hive%' AND "title"."id" <= 1754720 AND "char_name"."id" IS NOT NULL;