SELECT MAX("title"."kind_id") as agg_0, MIN("title"."kind_id") as agg_1 FROM "char_name" LEFT OUTER JOIN "cast_info" ON "char_name"."id" = "cast_info"."person_role_id" LEFT OUTER JOIN "title" ON "cast_info"."movie_id" = "title"."id"  WHERE "title"."season_nr" BETWEEN 4.632458395436502 AND 259.86263199449127 AND "char_name"."id" >= 579191 AND "cast_info"."id" >= 26960828 AND "char_name"."surname_pcode" LIKE '%M%5%' AND "cast_info"."note" LIKE '%(v%oice)%';