SELECT AVG("title"."kind_id") as agg_0 FROM "company_type" LEFT OUTER JOIN "movie_companies" ON "company_type"."id" = "movie_companies"."company_type_id" LEFT OUTER JOIN "title" ON "movie_companies"."movie_id" = "title"."id" LEFT OUTER JOIN "company_name" ON "movie_companies"."company_id" = "company_name"."id"  WHERE "title"."imdb_index" IN ('II', 'IV') AND "company_name"."name" LIKE '%Televi%sion%' AND ("title"."title" LIKE '%and%' OR "title"."title" LIKE '%t%o%');