SELECT MAX("movie_keyword"."movie_id") as agg_0 FROM "kind_type" LEFT OUTER JOIN "title" ON "kind_type"."id" = "title"."kind_id" LEFT OUTER JOIN "cast_info" ON "title"."id" = "cast_info"."movie_id" LEFT OUTER JOIN "char_name" ON "cast_info"."person_role_id" = "char_name"."id" LEFT OUTER JOIN "movie_keyword" ON "title"."id" = "movie_keyword"."movie_id"  WHERE "char_name"."surname_pcode" LIKE '%M%5%' AND "title"."title" NOT LIKE '%i%n%' AND "char_name"."id" <= 2118371 AND "char_name"."name_pcode_nf" LIKE '%H5241%';