SELECT MIN("title"."episode_of_id") as agg_0, AVG("movie_companies"."company_id") as agg_1 FROM "kind_type" LEFT OUTER JOIN "title" ON "kind_type"."id" = "title"."kind_id" LEFT OUTER JOIN "movie_keyword" ON "title"."id" = "movie_keyword"."movie_id" LEFT OUTER JOIN "cast_info" ON "title"."id" = "cast_info"."movie_id" LEFT OUTER JOIN "movie_info" ON "title"."id" = "movie_info"."movie_id" LEFT OUTER JOIN "aka_name" ON "cast_info"."person_id" = "aka_name"."id" LEFT OUTER JOIN "name" ON "aka_name"."person_id" = "name"."id" LEFT OUTER JOIN "movie_companies" ON "title"."id" = "movie_companies"."movie_id" LEFT OUTER JOIN "person_info" ON "name"."id" = "person_info"."person_id"  WHERE "name"."id" BETWEEN 428863 AND 835424 AND "cast_info"."person_role_id" IS NOT NULL AND "kind_type"."kind" IN ('tv mini series', 'tv movie', 'video game', 'video movie') AND ("aka_name"."imdb_index" = '%IV%' OR "aka_name"."name" NOT LIKE '%Bro%wn,%') AND "movie_companies"."note" LIKE '%(worldwide)%' AND "movie_info"."info" LIKE '%D%utch%' AND "movie_keyword"."movie_id" IS NOT NULL AND "person_info"."person_id" IS NOT NULL;