SELECT COUNT(*) as agg_0, AVG("cast_info"."nr_order") as agg_1 FROM "cast_info" LEFT OUTER JOIN "aka_name" ON "cast_info"."person_id" = "aka_name"."id" LEFT OUTER JOIN "char_name" ON "cast_info"."person_role_id" = "char_name"."id"  WHERE "aka_name"."surname_pcode" IN ('M6', 'M2', 'B65', 'L2', 'B2', 'J52', 'S53', 'L', 'B62') AND "cast_info"."id" >= 19496126 AND "char_name"."id" BETWEEN 1487583 AND 2550782;