SELECT AVG("movie_info"."movie_id") as agg_0, COUNT(*) as agg_1 FROM "movie_keyword" LEFT OUTER JOIN "title" ON "movie_keyword"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_info" ON "title"."id" = "movie_info"."movie_id"  WHERE ("movie_info"."info" NOT LIKE '%English%' OR ("movie_info"."info" NOT LIKE '%Spani%sh%' AND "movie_info"."info" LIKE '%German%')) AND ("title"."episode_of_id" BETWEEN 1323584.7807049272 AND 2255255.1512841242 OR "title"."episode_of_id" >= 771811.4452237011) AND "title"."title" LIKE '%de%' AND ("movie_info"."movie_id" <= 400566 OR "movie_info"."movie_id" >= 999472);