SELECT MAX("movie_companies"."company_type_id") as agg_0 FROM "movie_companies" LEFT OUTER JOIN "company_type" ON "movie_companies"."company_type_id" = "company_type"."id" LEFT OUTER JOIN "company_name" ON "movie_companies"."company_id" = "company_name"."id"  WHERE "company_type"."kind" != '%miscellaneous companies%' AND "company_name"."id" >= 133102 AND "company_name"."country_code" != '%[kw]%' AND ("company_type"."kind" IN ('special effects companies', 'production companies') OR "company_type"."kind" NOT LIKE '%companies%');