SELECT COUNT(*) as agg_0, AVG("keyword"."id") as agg_1 FROM "keyword" LEFT OUTER JOIN "movie_keyword" ON "keyword"."id" = "movie_keyword"."keyword_id" LEFT OUTER JOIN "title" ON "movie_keyword"."movie_id" = "title"."id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id"  WHERE "kind_type"."id" <= 3 AND "movie_keyword"."keyword_id" <= 3274 AND "keyword"."phonetic_code" LIKE '%R16%52%';