SELECT SUM("person_info"."info_type_id") as agg_0, MIN("name"."id") as agg_1 FROM "name" LEFT OUTER JOIN "aka_name" ON "name"."id" = "aka_name"."person_id" LEFT OUTER JOIN "person_info" ON "name"."id" = "person_info"."person_id" LEFT OUTER JOIN "cast_info" ON "aka_name"."id" = "cast_info"."person_id"  WHERE "aka_name"."name" LIKE '%J.%' AND ("aka_name"."surname_pcode" IN ('B62', 'J52', 'B2') OR "aka_name"."surname_pcode" LIKE '%B%2%') AND "name"."surname_pcode" NOT LIKE '%A25%' AND "person_info"."info_type_id" >= 25 AND "cast_info"."person_id" IS NOT NULL;