SELECT MIN("aka_name"."id") as agg_0, SUM("name"."id") as agg_1 FROM "cast_info" LEFT OUTER JOIN "aka_name" ON "cast_info"."person_id" = "aka_name"."id" LEFT OUTER JOIN "char_name" ON "cast_info"."person_role_id" = "char_name"."id" LEFT OUTER JOIN "name" ON "aka_name"."person_id" = "name"."id" LEFT OUTER JOIN "person_info" ON "name"."id" = "person_info"."person_id" LEFT OUTER JOIN "title" ON "cast_info"."movie_id" = "title"."id"  WHERE "cast_info"."note" LIKE '%Adler)%' AND "aka_name"."name_pcode_cf" NOT LIKE '%A5362%' AND "aka_name"."name" NOT LIKE '%David%' AND "char_name"."id" IS NOT NULL AND "person_info"."person_id" IS NOT NULL AND "title"."id" IS NOT NULL;