SELECT AVG("movie_companies"."company_type_id") as agg_0, MAX("title"."production_year") as agg_1 FROM "movie_info_idx" LEFT OUTER JOIN "title" ON "movie_info_idx"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_keyword" ON "title"."id" = "movie_keyword"."movie_id" LEFT OUTER JOIN "movie_companies" ON "title"."id" = "movie_companies"."movie_id"  WHERE "movie_info_idx"."info" LIKE '%6.%8%' AND "movie_keyword"."movie_id" >= 2069476 AND "movie_companies"."note" LIKE '%(2%012)%' AND "movie_keyword"."id" BETWEEN 1833973 AND 4346194;