SELECT SUM("movie_companies"."company_type_id") as agg_0, MAX("kind_type"."id") as agg_1 FROM "movie_keyword" LEFT OUTER JOIN "title" ON "movie_keyword"."movie_id" = "title"."id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id" LEFT OUTER JOIN "keyword" ON "movie_keyword"."keyword_id" = "keyword"."id" LEFT OUTER JOIN "movie_companies" ON "title"."id" = "movie_companies"."movie_id"  WHERE "keyword"."phonetic_code" LIKE '%R1652%' AND "title"."title" NOT LIKE '%The%' AND "movie_companies"."note" LIKE '%(2010%)%' AND ("movie_keyword"."movie_id" BETWEEN 1146224 AND 2311682 OR ("movie_keyword"."movie_id" <= 1950533 AND "movie_keyword"."movie_id" >= 2338388)) AND "title"."episode_nr" <= 19.5366482566442;