SELECT MAX("name"."id") as agg_0 FROM "aka_name" LEFT OUTER JOIN "name" ON "aka_name"."person_id" = "name"."id" LEFT OUTER JOIN "cast_info" ON "aka_name"."id" = "cast_info"."person_id" LEFT OUTER JOIN "char_name" ON "cast_info"."person_role_id" = "char_name"."id" LEFT OUTER JOIN "title" ON "cast_info"."movie_id" = "title"."id"  WHERE "char_name"."id" BETWEEN 794112 AND 2780472 AND "cast_info"."note" LIKE '%(uncredited)%' AND "name"."surname_pcode" IS NULL AND "char_name"."name_pcode_nf" NOT LIKE '%H5241%' AND "aka_name"."surname_pcode" NOT LIKE '%B%6%' AND "char_name"."name" LIKE '%Th%e%' AND "title"."id" IS NOT NULL;