SELECT COUNT(*) as agg_0, COUNT(*) as agg_1 FROM "movie_companies" LEFT OUTER JOIN "title" ON "movie_companies"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_info" ON "title"."id" = "movie_info"."movie_id" LEFT OUTER JOIN "movie_keyword" ON "title"."id" = "movie_keyword"."movie_id" LEFT OUTER JOIN "cast_info" ON "title"."id" = "cast_info"."movie_id" LEFT OUTER JOIN "movie_info_idx" ON "title"."id" = "movie_info_idx"."movie_id" LEFT OUTER JOIN "info_type" ON "movie_info_idx"."info_type_id" = "info_type"."id" LEFT OUTER JOIN "keyword" ON "movie_keyword"."keyword_id" = "keyword"."id" LEFT OUTER JOIN "company_type" ON "movie_companies"."company_type_id" = "company_type"."id"  WHERE ("keyword"."id" BETWEEN 35970 AND 83855 OR "keyword"."phonetic_code" LIKE '%R1652%' OR "keyword"."id" >= 22403) AND ("movie_companies"."note" NOT LIKE '%(Hungary)%' OR "movie_companies"."note" LIKE '%(2004)%' OR "movie_companies"."note" LIKE '%(2009%)%') AND "info_type"."info" LIKE '%color%' AND "movie_info"."movie_id" IS NOT NULL AND "cast_info"."movie_id" IS NOT NULL AND "company_type"."id" IS NOT NULL;