SELECT SUM("title"."episode_of_id") as agg_0 FROM "keyword" LEFT OUTER JOIN "movie_keyword" ON "keyword"."id" = "movie_keyword"."keyword_id" LEFT OUTER JOIN "title" ON "movie_keyword"."movie_id" = "title"."id"  WHERE "movie_keyword"."keyword_id" >= 168 AND "title"."title" LIKE '%of%' AND "title"."kind_id" BETWEEN 1 AND 7 AND ("keyword"."phonetic_code" NOT LIKE '%R1%652%' OR "keyword"."phonetic_code" LIKE '%R16%52%') AND "title"."imdb_index" != '%XXIII%' AND "movie_keyword"."movie_id" >= 163585;