SELECT AVG("title"."episode_nr") as agg_0, MIN("title"."id") as agg_1 FROM "company_type" LEFT OUTER JOIN "movie_companies" ON "company_type"."id" = "movie_companies"."company_type_id" LEFT OUTER JOIN "company_name" ON "movie_companies"."company_id" = "company_name"."id" LEFT OUTER JOIN "title" ON "movie_companies"."movie_id" = "title"."id" LEFT OUTER JOIN "cast_info" ON "title"."id" = "cast_info"."movie_id" LEFT OUTER JOIN "aka_name" ON "cast_info"."person_id" = "aka_name"."id" LEFT OUTER JOIN "name" ON "aka_name"."person_id" = "name"."id" LEFT OUTER JOIN "movie_keyword" ON "title"."id" = "movie_keyword"."movie_id" LEFT OUTER JOIN "keyword" ON "movie_keyword"."keyword_id" = "keyword"."id"  WHERE "company_type"."id" BETWEEN 1 AND 3 AND "movie_keyword"."movie_id" >= 689724 AND "name"."imdb_index" NOT LIKE '%II%I%' AND "aka_name"."name_pcode_cf" NOT LIKE '%A5%362%' AND "company_name"."country_code" != '%[lt]%' AND "keyword"."id" IS NOT NULL;