SELECT AVG("kind_type"."id") as agg_0 FROM "movie_info" LEFT OUTER JOIN "title" ON "movie_info"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_companies" ON "title"."id" = "movie_companies"."movie_id" LEFT OUTER JOIN "company_name" ON "movie_companies"."company_id" = "company_name"."id" LEFT OUTER JOIN "movie_info_idx" ON "title"."id" = "movie_info_idx"."movie_id" LEFT OUTER JOIN "company_type" ON "movie_companies"."company_type_id" = "company_type"."id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id" LEFT OUTER JOIN "cast_info" ON "title"."id" = "cast_info"."movie_id" LEFT OUTER JOIN "info_type" ON "movie_info_idx"."info_type_id" = "info_type"."id"  WHERE "kind_type"."kind" NOT LIKE '%t%v%' AND "kind_type"."id" BETWEEN 3 AND 5 AND "movie_info"."info" LIKE '%Eng%lish%' AND "company_type"."kind" NOT LIKE '%compan%ies%' AND ("info_type"."id" >= 2 OR ("info_type"."id" >= 95 AND "info_type"."id" BETWEEN 5 AND 71)) AND "company_name"."id" IS NOT NULL AND "cast_info"."movie_id" IS NOT NULL;