SELECT MIN("person_info"."info_type_id") as agg_0 FROM "char_name" LEFT OUTER JOIN "cast_info" ON "char_name"."id" = "cast_info"."person_role_id" LEFT OUTER JOIN "aka_name" ON "cast_info"."person_id" = "aka_name"."id" LEFT OUTER JOIN "name" ON "aka_name"."person_id" = "name"."id" LEFT OUTER JOIN "person_info" ON "name"."id" = "person_info"."person_id" LEFT OUTER JOIN "title" ON "cast_info"."movie_id" = "title"."id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id" LEFT OUTER JOIN "movie_info" ON "title"."id" = "movie_info"."movie_id" LEFT OUTER JOIN "movie_keyword" ON "title"."id" = "movie_keyword"."movie_id"  WHERE "aka_name"."name_pcode_cf" LIKE '%A5362%' AND "char_name"."surname_pcode" NOT LIKE '%M5%' AND "person_info"."info" NOT LIKE '%this%' AND "movie_info"."info" LIKE '%Po%rtuguese%' AND "person_info"."id" <= 2616385 AND "kind_type"."kind" LIKE '%m%ovie%' AND "movie_keyword"."movie_id" IS NOT NULL;