SELECT SUM("title"."episode_of_id") as agg_0, MIN("title"."season_nr") as agg_1 FROM "info_type" LEFT OUTER JOIN "movie_info_idx" ON "info_type"."id" = "movie_info_idx"."info_type_id" LEFT OUTER JOIN "title" ON "movie_info_idx"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_info" ON "title"."id" = "movie_info"."movie_id"  WHERE "movie_info_idx"."info" NOT LIKE '%7.1%' AND "movie_info_idx"."info_type_id" = 99 AND "title"."title" LIKE '%of%' AND "info_type"."info" NOT LIKE '%audio%' AND "movie_info"."movie_id" IS NOT NULL;