SELECT AVG("title"."id") as agg_0, SUM("title"."production_year") as agg_1 FROM "kind_type" LEFT OUTER JOIN "title" ON "kind_type"."id" = "title"."kind_id" LEFT OUTER JOIN "cast_info" ON "title"."id" = "cast_info"."movie_id" LEFT OUTER JOIN "movie_info_idx" ON "title"."id" = "movie_info_idx"."movie_id" LEFT OUTER JOIN "movie_companies" ON "title"."id" = "movie_companies"."movie_id"  WHERE ("movie_companies"."movie_id" >= 2257855 OR "movie_companies"."movie_id" <= 820959) AND ("title"."title" NOT LIKE '%and%' OR "title"."kind_id" <= 7) AND "kind_type"."kind" LIKE '%t%v%' AND "cast_info"."note" NOT LIKE '%(uncredited)%' AND "cast_info"."person_role_id" BETWEEN 48337.3691898198 AND 101053.06174494281 AND "movie_info_idx"."movie_id" IS NOT NULL;