SELECT AVG("title"."production_year") as agg_0 FROM "title" LEFT OUTER JOIN "movie_info" ON "title"."id" = "movie_info"."movie_id" LEFT OUTER JOIN "movie_info_idx" ON "title"."id" = "movie_info_idx"."movie_id" LEFT OUTER JOIN "movie_companies" ON "title"."id" = "movie_companies"."movie_id"  WHERE ("movie_companies"."company_type_id" BETWEEN 2 AND 3 OR "movie_companies"."company_id" >= 8100 OR "movie_companies"."company_type_id" >= 1) AND "movie_info"."movie_id" >= 2370977 AND "movie_info_idx"."info_type_id" BETWEEN 99 AND 101 AND "movie_info_idx"."info" LIKE '%7.3%' AND "movie_companies"."id" >= 1903638 AND ("title"."title" NOT LIKE '%de%' OR "title"."title" NOT LIKE '%an%d%');