SELECT AVG("title"."season_nr") as agg_0, SUM("title"."id") as agg_1 FROM "movie_info_idx" LEFT OUTER JOIN "info_type" ON "movie_info_idx"."info_type_id" = "info_type"."id" LEFT OUTER JOIN "title" ON "movie_info_idx"."movie_id" = "title"."id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id"  WHERE "movie_info_idx"."info" LIKE '%7.1%' AND "title"."title" NOT LIKE '%to%' AND "movie_info_idx"."id" >= 709160 AND ("kind_type"."id" BETWEEN 1 AND 3 OR "kind_type"."id" = 3) AND "info_type"."id" IS NOT NULL;