SELECT AVG("movie_keyword"."keyword_id") as agg_0 FROM "name" LEFT OUTER JOIN "person_info" ON "name"."id" = "person_info"."person_id" LEFT OUTER JOIN "aka_name" ON "name"."id" = "aka_name"."person_id" LEFT OUTER JOIN "cast_info" ON "aka_name"."id" = "cast_info"."person_id" LEFT OUTER JOIN "char_name" ON "cast_info"."person_role_id" = "char_name"."id" LEFT OUTER JOIN "title" ON "cast_info"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_keyword" ON "title"."id" = "movie_keyword"."movie_id"  WHERE "movie_keyword"."keyword_id" <= 63473 AND "name"."imdb_index" IN ('VII', 'VIII', 'III', 'II', 'VI', 'X', 'I', 'IV') AND "movie_keyword"."id" >= 3385706 AND "cast_info"."role_id" != 1 AND "aka_name"."surname_pcode" NOT LIKE '%B6%5%' AND "name"."imdb_index" LIKE '%III%' AND "person_info"."person_id" IS NOT NULL AND "char_name"."id" IS NOT NULL;