SELECT AVG("keyword"."id") as agg_0, MAX("movie_keyword"."id") as agg_1 FROM "keyword" LEFT OUTER JOIN "movie_keyword" ON "keyword"."id" = "movie_keyword"."keyword_id" LEFT OUTER JOIN "title" ON "movie_keyword"."movie_id" = "title"."id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id"  WHERE "movie_keyword"."movie_id" >= 2366764 AND ("kind_type"."kind" IN ('video movie', 'video game', 'movie', 'tv movie') OR "kind_type"."kind" LIKE '%video%' OR "kind_type"."kind" IN ('tv series', 'movie', 'tv movie', 'episode', 'video game', 'video movie')) AND "movie_keyword"."keyword_id" BETWEEN 109 AND 3121 AND ("title"."id" BETWEEN 78199 AND 1194442 OR ("title"."production_year" >= 2005.9496197344836 AND "title"."id" BETWEEN 829503 AND 2280976));