SELECT MIN("title"."season_nr") as agg_0 FROM "company_name" LEFT OUTER JOIN "movie_companies" ON "company_name"."id" = "movie_companies"."company_id" LEFT OUTER JOIN "title" ON "movie_companies"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_info" ON "title"."id" = "movie_info"."movie_id"  WHERE "company_name"."name" NOT LIKE '%Television%' AND "movie_companies"."note" LIKE '%(Philippine%s)%' AND "title"."title" NOT LIKE '%P%art%' AND ("movie_companies"."movie_id" BETWEEN 556450 AND 2139968 OR ("movie_companies"."company_type_id" <= 2 AND "movie_companies"."movie_id" <= 1225909)) AND "company_name"."country_code" IN ('[ca]', '[us]', '[gb]', '[es]', '[in]', '[jp]', '[au]', '[fr]', '[it]') AND "movie_info"."movie_id" IS NOT NULL;