SELECT AVG("movie_companies"."company_type_id") as agg_0, AVG("movie_info_idx"."movie_id") as agg_1 FROM "movie_keyword" LEFT OUTER JOIN "title" ON "movie_keyword"."movie_id" = "title"."id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id" LEFT OUTER JOIN "movie_info_idx" ON "title"."id" = "movie_info_idx"."movie_id" LEFT OUTER JOIN "keyword" ON "movie_keyword"."keyword_id" = "keyword"."id" LEFT OUTER JOIN "cast_info" ON "title"."id" = "cast_info"."movie_id" LEFT OUTER JOIN "movie_companies" ON "title"."id" = "movie_companies"."movie_id"  WHERE ("kind_type"."id" BETWEEN 2 AND 6 OR ("kind_type"."id" = 1 AND "kind_type"."id" <= 5)) AND "movie_info_idx"."info" LIKE '%7.2%' AND ("keyword"."id" BETWEEN 7949 AND 88277 OR "keyword"."id" BETWEEN 2248 AND 82155 OR "keyword"."id" >= 34243) AND "movie_companies"."note" LIKE '%(2002)%' AND "title"."kind_id" BETWEEN 1 AND 346 AND "cast_info"."movie_id" IS NOT NULL;