SELECT MIN("title"."episode_of_id") as agg_0, SUM("title"."season_nr") as agg_1 FROM "kind_type" LEFT OUTER JOIN "title" ON "kind_type"."id" = "title"."kind_id" LEFT OUTER JOIN "cast_info" ON "title"."id" = "cast_info"."movie_id" LEFT OUTER JOIN "movie_info" ON "title"."id" = "movie_info"."movie_id" LEFT OUTER JOIN "movie_keyword" ON "title"."id" = "movie_keyword"."movie_id" LEFT OUTER JOIN "movie_info_idx" ON "title"."id" = "movie_info_idx"."movie_id" LEFT OUTER JOIN "aka_name" ON "cast_info"."person_id" = "aka_name"."id"  WHERE ("movie_info"."id" >= 7612682 OR "movie_info"."info_type_id" >= 16 OR "movie_info"."id" <= 14089869) AND "title"."title" NOT LIKE '%The%' AND ("movie_info"."info" LIKE '%Portugue%se%' OR "movie_info"."info" LIKE '%D%utch%') AND "movie_keyword"."movie_id" IS NOT NULL AND "movie_info_idx"."movie_id" IS NOT NULL AND "aka_name"."id" IS NOT NULL;