SELECT MIN("title"."season_nr") as agg_0, MAX("movie_info"."info_type_id") as agg_1 FROM "movie_info" LEFT OUTER JOIN "title" ON "movie_info"."movie_id" = "title"."id" LEFT OUTER JOIN "cast_info" ON "title"."id" = "cast_info"."movie_id" LEFT OUTER JOIN "movie_keyword" ON "title"."id" = "movie_keyword"."movie_id" LEFT OUTER JOIN "aka_name" ON "cast_info"."person_id" = "aka_name"."id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id"  WHERE "kind_type"."id" BETWEEN 1 AND 4 AND "movie_info"."info" LIKE '%English%' AND "kind_type"."kind" LIKE '%movie%' AND ("movie_info"."info_type_id" != 1 OR "movie_info"."movie_id" BETWEEN 1463230 AND 1812455 OR "movie_info"."info_type_id" <= 11) AND "aka_name"."surname_pcode" LIKE '%B6%' AND "movie_keyword"."movie_id" IS NOT NULL;