SELECT MAX("person_info"."person_id") as agg_0, AVG("person_info"."info_type_id") as agg_1 FROM "name" LEFT OUTER JOIN "person_info" ON "name"."id" = "person_info"."person_id" LEFT OUTER JOIN "aka_name" ON "name"."id" = "aka_name"."person_id"  WHERE "name"."surname_pcode" != '%J46%' AND "person_info"."info" LIKE '%be%' AND "aka_name"."surname_pcode" IN ('B65', 'M2', 'B62', 'B2', 'L', 'J52', 'S53');