SELECT AVG("aka_name"."person_id") as agg_0, MIN("name"."id") as agg_1 FROM "name" LEFT OUTER JOIN "person_info" ON "name"."id" = "person_info"."person_id" LEFT OUTER JOIN "aka_name" ON "name"."id" = "aka_name"."person_id" LEFT OUTER JOIN "cast_info" ON "aka_name"."id" = "cast_info"."person_id"  WHERE "person_info"."id" >= 1712964 AND "name"."surname_pcode" IN ('M2', 'R2') AND "aka_name"."person_id" <= 2337525 AND ("aka_name"."id" >= 518192 OR "aka_name"."name" NOT LIKE '%Brow%n,%' OR "aka_name"."id" >= 362487) AND "cast_info"."person_id" IS NOT NULL;