SELECT MIN("movie_keyword"."id") as agg_0, MIN("aka_name"."id") as agg_1 FROM "kind_type" LEFT OUTER JOIN "title" ON "kind_type"."id" = "title"."kind_id" LEFT OUTER JOIN "movie_keyword" ON "title"."id" = "movie_keyword"."movie_id" LEFT OUTER JOIN "cast_info" ON "title"."id" = "cast_info"."movie_id" LEFT OUTER JOIN "movie_info_idx" ON "title"."id" = "movie_info_idx"."movie_id" LEFT OUTER JOIN "movie_info" ON "title"."id" = "movie_info"."movie_id" LEFT OUTER JOIN "aka_name" ON "cast_info"."person_id" = "aka_name"."id"  WHERE "title"."episode_nr" IS NOT NULL AND "movie_keyword"."id" BETWEEN 1480181 AND 2075003 AND "movie_info_idx"."info_type_id" != 99 AND "movie_info_idx"."info" NOT LIKE '%7%.6%' AND "movie_info"."movie_id" IS NOT NULL;