SELECT MIN("title"."id") as agg_0, SUM("title"."kind_id") as agg_1 FROM "movie_companies" LEFT OUTER JOIN "company_name" ON "movie_companies"."company_id" = "company_name"."id" LEFT OUTER JOIN "company_type" ON "movie_companies"."company_type_id" = "company_type"."id" LEFT OUTER JOIN "title" ON "movie_companies"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_keyword" ON "title"."id" = "movie_keyword"."movie_id"  WHERE "company_name"."name" NOT LIKE '%Pictures%' AND "movie_companies"."id" >= 551583 AND "company_type"."id" BETWEEN 1 AND 2 AND "movie_keyword"."id" >= 2885779 AND "movie_keyword"."keyword_id" <= 2618;