SELECT MIN("title"."season_nr") as agg_0, SUM("movie_info"."movie_id") as agg_1 FROM "cast_info" LEFT OUTER JOIN "title" ON "cast_info"."movie_id" = "title"."id" LEFT OUTER JOIN "aka_name" ON "cast_info"."person_id" = "aka_name"."id" LEFT OUTER JOIN "movie_info" ON "title"."id" = "movie_info"."movie_id"  WHERE "aka_name"."surname_pcode" IN ('L2', 'M2') AND "movie_info"."id" >= 12092586 AND "movie_info"."info" NOT LIKE '%Spanish%';