SELECT COUNT(*) as agg_0, SUM("movie_info_idx"."movie_id") as agg_1 FROM "movie_keyword" LEFT OUTER JOIN "keyword" ON "movie_keyword"."keyword_id" = "keyword"."id" LEFT OUTER JOIN "title" ON "movie_keyword"."movie_id" = "title"."id" LEFT OUTER JOIN "movie_info" ON "title"."id" = "movie_info"."movie_id" LEFT OUTER JOIN "movie_info_idx" ON "title"."id" = "movie_info_idx"."movie_id" LEFT OUTER JOIN "kind_type" ON "title"."kind_id" = "kind_type"."id"  WHERE "movie_info_idx"."info" LIKE '%1%0%' AND "keyword"."phonetic_code" NOT LIKE '%R165%2%' AND "movie_keyword"."id" >= 2569053 AND "movie_info"."movie_id" IS NOT NULL AND "kind_type"."id" IS NOT NULL;