SELECT MAX("movie_info"."info_type_id") as agg_0, MIN("movie_companies"."company_id") as agg_1 FROM "kind_type" LEFT OUTER JOIN "title" ON "kind_type"."id" = "title"."kind_id" LEFT OUTER JOIN "movie_info_idx" ON "title"."id" = "movie_info_idx"."movie_id" LEFT OUTER JOIN "movie_info" ON "title"."id" = "movie_info"."movie_id" LEFT OUTER JOIN "movie_companies" ON "title"."id" = "movie_companies"."movie_id" LEFT OUTER JOIN "cast_info" ON "title"."id" = "cast_info"."movie_id"  WHERE "movie_info_idx"."info" NOT LIKE '%7.5%' AND "cast_info"."note" LIKE '%(as%' AND "movie_companies"."note" NOT LIKE '%(2010%)%' AND "movie_info"."info_type_id" = 2 AND "movie_companies"."movie_id" >= 2354105;