SELECT AVG("title"."kind_id") as agg_0 FROM "person_info" LEFT OUTER JOIN "name" ON "person_info"."person_id" = "name"."id" LEFT OUTER JOIN "aka_name" ON "name"."id" = "aka_name"."person_id" LEFT OUTER JOIN "cast_info" ON "aka_name"."id" = "cast_info"."person_id" LEFT OUTER JOIN "title" ON "cast_info"."movie_id" = "title"."id"  WHERE "person_info"."id" BETWEEN 1423467 AND 1544848 AND "person_info"."info" LIKE '%t%he%' AND "aka_name"."name" NOT LIKE '%Michae%l%' AND "cast_info"."movie_id" >= 1303079 AND "cast_info"."note" LIKE '%(as%';